import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [sections, setSections] = useState([]);

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/projects/${projectName}/sections`);
      setSections(response.data);
    } catch (error) {
      console.error('Error fetching sections:', error);
    }
  };

  const addSection = async (sectionName) => {
    try {
      await axios.post(`http://localhost:5000/projects/${projectName}/sections`, {
        section_name: sectionName
      });
      fetchSections(); // Refresh sections after addition
    } catch (error) {
      console.error('Error adding section:', error);
    }
  };

  const deleteSection = async (sectionId) => {
    try {
      await axios.delete(`http://localhost:5000/sections/${sectionId}`);
      fetchSections(); // Refresh sections after deletion
    } catch (error) {
      console.error('Error deleting section:', error);
    }
  };

  const renderSections = () => {
    return sections.map(section => (
      <div key={section.section_id} className="section">
        <h3>{section.section_name}</h3>
        {/* Render subsections and documents for each section */}
      </div>
    ));
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <div className="add-section-form">
        <h3>Add Section</h3>
        <form onSubmit={(e) => {
          e.preventDefault();
          const sectionName = e.target.sectionName.value;
          if (sectionName) {
            addSection(sectionName);
            e.target.reset();
          }
        }}>
          <input type="text" name="sectionName" placeholder="Enter section name" required />
          <button type="submit">Add</button>
        </form>
      </div>

      <div className="sections">
        {renderSections()}
      </div>
    </div>
  );
};

export default ProjectDetail;
