import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import fetchProjectName from '../utils/fetchProjectName';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();

  const [projectName, setProjectName] = useState('');
  const [sections, setSections] = useState([]);
  const [newSectionName, setNewSectionName] = useState('');
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [currentSectionId, setCurrentSectionId] = useState(null);
  const [currentSubsectionId, setCurrentSubsectionId] = useState(null);
  const [isSectionModalOpen, setIsSectionModalOpen] = useState(false);
  const [isSubsectionModalOpen, setIsSubsectionModalOpen] = useState(false);
  const [isDocumentModalOpen, setIsDocumentModalOpen] = useState(false);
  const [newDocumentName, setNewDocumentName] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      if (projectId) {
        const success = await fetchProjectDetails();
        if (success) {
          console.log('Details fetched');
        } else {
          console.log('No details fetched');
        }
      }
    };
    fetchData();
  }, [projectId]);

  const fetchProjectDetails = async () => {
    try {
      const projectName = await fetchProjectName(projectId);
      setProjectName(projectName);
      await fetchSections(Number(projectId));
      return true;
    } catch (error) {
      console.error('Error fetching project details:', error);
      return false;
    }
  };

  const fetchSections = async (numericProjectId) => {
    try {
      const sectionsResponse = await axios.get(`http://localhost:5000/api/projects/${numericProjectId}/sections`);
      const sectionsWithSubsections = await Promise.all(sectionsResponse.data.map(async (section) => {
        const subsectionsResponse = await axios.get(`http://localhost:5000/api/sections/${section.section_id}/subsections`);
        const subsectionsWithDocuments = await Promise.all(subsectionsResponse.data.map(async (subsection) => {
          const documentsResponse = await axios.get(`http://localhost:5000/api/subsections/${subsection.subsection_id}/documents`);
          return { ...subsection, documents: documentsResponse.data };
        }));
        return { ...section, subsections: subsectionsWithDocuments };
      }));
      setSections(sectionsWithSubsections);
    } catch (error) {
      console.error('Error fetching sections:', error);
      throw error;
    }
  };

  const handleAddSection = async () => {
    try {
      const numericProjectId = Number(projectId);
      const response = await axios.post('http://localhost:5000/api/sections', { section_name: newSectionName, project_id: numericProjectId });
      const newSection = response.data;
      setSections([...sections, { ...newSection, subsections: [] }]);
      setIsSectionModalOpen(false);
      setNewSectionName('');
    } catch (error) {
      console.error('Error adding section:', error);
    }
  };

  const handleAddSubsection = async () => {
    try {
      console.log("Current Section ID:", currentSectionId);
      const response = await axios.post('http://localhost:5000/api/subsections', { subsection_name: newSubsectionName, section_id: currentSectionId });
      const newSubsection = response.data;
      setSections(prevSections => {
        return prevSections.map(section => {
          if (section.section_id === currentSectionId) {
            return { ...section, subsections: [...section.subsections, { ...newSubsection, documents: [] }] };
          }
          return section;
        });
      });
      setIsSubsectionModalOpen(false);
      setNewSubsectionName('');
    } catch (error) {
      console.error('Error adding subsection:', error);
    }
  };

  const handleSubmitDocument = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/documents', {
        document_name: newDocumentName,
        subsection_id: currentSubsectionId,
      });
      setSections(prevSections => {
        return prevSections.map(section => {
          const updatedSubsections = section.subsections.map(subsection => {
            if (subsection.subsection_id === currentSubsectionId) {
              return { ...subsection, documents: [...subsection.documents, { document_name: newDocumentName }] };
            }
            return subsection;
          });
          return { ...section, subsections: updatedSubsections };
        });
      });
      setIsDocumentModalOpen(false);
      setNewDocumentName('');
    } catch (error) {
      console.error('Error adding document:', error);
    }
  };

  const handleDeleteSection = async (sectionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/sections/${sectionId}`);
      setSections(prevSections => prevSections.filter(section => section.section_id !== sectionId));
    } catch (error) {
      console.error('Error deleting section:', error);
    }
  };
  
  const handleDeleteSubsection = async (subsectionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/subsections/${subsectionId}`);
      setSections(prevSections =>
        prevSections.map(section =>
          ({
            ...section,
            subsections: section.subsections.filter(subsection => subsection.subsection_id !== subsectionId)
          })
        )
      );
    } catch (error) {
      console.error('Error deleting subsection:', error);
    }
  };
  
  const handleDeleteDocument = async (documentId, subsectionId) => {
    try {
      console.log('Deleting document with ID:', documentId, 'in subsection ID:', subsectionId);
      await axios.delete(`http://localhost:5000/api/documents/${documentId}/${subsectionId}`);
      setSections(prevSections =>
        prevSections.map(section =>
          ({
            ...section,
            subsections: section.subsections.map(subsection =>
              ({
                ...subsection,
                documents: subsection.documents.filter(document => document.document_id !== documentId)
              })
            )
          })
        )
      );
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  };

  const handleBack = () => {
    navigate(-1);
  };

  return (
    <div className="project-detail-container">
      <h1 style={{ textAlign: "center" }}>Project Details</h1>
      <h2 className="project-title">Project Title: {projectName}</h2>
      <button onClick={handleBack} className="back-button">Back</button>
  
      <div className="sections-container">
        <button onClick={() => setIsSectionModalOpen(true)} className="add-button">Add Section</button>
        {sections.length > 0 ? (
          sections.map(section => (
            <div key={section.section_id} className="section">
            <h3 className="section-name">
              {section.section_name}
              <button className="delete-button" onClick={() => handleDeleteSection(section.id)}>Delete</button>
            </h3>
            <button onClick={() => { setCurrentSectionId(section.id); setIsSubsectionModalOpen(true); }} className="add-button">Add Subsection</button>

            <div className={currentSectionId === section.id ? "subsection-container show" : "subsection-container hidden"}>
              {section.subsections.length > 0 ? (
                section.subsections.map(subsection => (
                  <div key={subsection.id} className="subsection">
                    <h4 className="subsection-name">
                      {subsection.subsection_name}
                      <button className="delete-button" onClick={() => handleDeleteSubsection(subsection.id)}>Delete</button>
                    </h4>
                    <button onClick={() => { setCurrentSubsectionId(subsection.id); setIsDocumentModalOpen(true); }} className="add-button">Add Document</button>

                    <div className={currentSubsectionId === subsection.id ? "document-container show" : "document-container hidden"}>
                      {subsection.documents.length > 0 ? (
                        subsection.documents.map(document => (
                          <div key={document.id} className="document">
                            <p>{document.document_name}</p>
                            <button className="delete-button" onClick={() => handleDeleteDocument(document.id, subsection.id)}>Delete</button>
                          </div>
                        ))
                      ) : (
                        <p>No documents available</p>
                      )}
                    </div>
                  </div>
                ))
              ) : (
                <p>No subsections available</p>
              )}
            </div>
          </div>
        ))
      ) : (
        <p>No sections available</p>
      )}
    </div>

    {isSectionModalOpen && (
      <div className="modal-overlay-section" onClick={() => setIsSectionModalOpen(false)}>
        <div className="add-section-container" onClick={(e) => e.stopPropagation()}>
          <h3>Add Section</h3>
          <input 
            type="text" 
            value={newSectionName} 
            onChange={(e) => setNewSectionName(e.target.value)} 
            className="section-input"
            placeholder="Enter Section Name"
          />
          <button onClick={handleAddSection} className="add-section-button">Save</button>
          <button onClick={() => setIsSectionModalOpen(false)} className="add-section-button">Cancel</button>
        </div>
      </div>
    )}

    {isSubsectionModalOpen && (
      <div className="modal-overlay-subsection" onClick={() => setIsSubsectionModalOpen(false)}>
        <div className="add-subsection-container" onClick={(e) => e.stopPropagation()}>
          <h3>Add Subsection</h3>
          <input 
            type="text" 
            value={newSubsectionName} 
            onChange={(e) => setNewSubsectionName(e.target.value)} 
            className="subsection-input"
            placeholder="Enter Subsection Name"
          />
          <button onClick={handleAddSubsection} className="add-subsection-button">Save</button>
          <button onClick={() => setIsSubsectionModalOpen(false)} className="add-subsection-button">Cancel</button>
        </div>
      </div>
    )}

    {isDocumentModalOpen && (
      <div className="modal-overlay-document" onClick={() => setIsDocumentModalOpen(false)}>
        <div className="add-document-container" onClick={(e) => e.stopPropagation()}>
          <h3>Add Document</h3>
          <form onSubmit={handleSubmitDocument}>
            <input 
              type="text" 
              value={newDocumentName} 
              onChange={(e) => setNewDocumentName(e.target.value)} 
              placeholder="Enter Document Name"
              className="document-input"
            />
            <button type="submit" className="add-document-button">Save</button>
            <button type="button" onClick={() => setIsDocumentModalOpen(false)} className="add-document-button">Cancel</button>
          </form>
        </div>
      </div>
    )}
  </div>
);
};

export default ProjectDetail;
