import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();

  const [projectName, setProjectName] = useState('');
  const [sections, setSections] = useState([]);
  const [newSectionName, setNewSectionName] = useState('');
  const [newSubsectionName, setNewSubsectionName] = useState('');

  useEffect(() => {
    if (projectId) {
      fetchProjectDetails();
    }
  }, [projectId]);

  const fetchProjectDetails = async () => {
    try {
      // Fetch project details
      const projectResponse = await axios.get(`/api/projects/${projectId}`);
      setProjectName(projectResponse.data.project_name);

      // Fetch sections
      const sectionsResponse = await axios.get(`/api/projects/${projectId}/sections`);
      setSections(sectionsResponse.data);
    } catch (error) {
      console.error('Error fetching project details:', error);
    }
  };

  const handleAddSection = async () => {
    try {
      // Add new section
      const response = await axios.post('/api/sections', { section_name: newSectionName, project_id: projectId });
      const newSection = response.data;
      setSections([...sections, newSection]);
      setNewSectionName('');
    } catch (error) {
      console.error('Error adding section:', error);
    }
  };

  const handleAddSubsection = async (sectionId) => {
    try {
      // Add new subsection
      const response = await axios.post('/api/subsections', { subsection_name: newSubsectionName, section_id: sectionId });
      const newSubsection = response.data;
      setSections(prevSections => {
        return prevSections.map(section => {
          if (section.section_id === sectionId) {
            return { ...section, subsections: [...section.subsections, newSubsection] };
          }
          return section;
        });
      });
      setNewSubsectionName('');
    } catch (error) {
      console.error('Error adding subsection:', error);
    }
  };

  return (
    <div>
      <h1>Project Detail</h1>
      <p>Project Title: {projectName}</p>
      
      {/* Render sections and subsections */}
      <div>
        {sections.map(section => (
          <div key={section.section_id}>
            <h2>{section.section_name}</h2>
            <ul>
              {section.subsections.map(subsection => (
                <li key={subsection.subsection_id}>
                  {subsection.subsection_name}
                </li>
              ))}
              {/* Button to add a new subsection */}
              <li>
                <input 
                  type="text" 
                  value={newSubsectionName} 
                  onChange={(e) => setNewSubsectionName(e.target.value)} 
                  placeholder="Enter subsection name"
                />
                <button onClick={() => handleAddSubsection(section.section_id)}>Add Subsection</button>
              </li>
            </ul>
          </div>
        ))}
      </div>

      {/* Form to add a new section */}
      <div>
        <input 
          type="text" 
          value={newSectionName} 
          onChange={(e) => setNewSectionName(e.target.value)} 
          placeholder="Enter section name"
        />
        <button onClick={handleAddSection}>Add Section</button>
      </div>
    </div>
  );
};

export default ProjectDetail;
