import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [sections, setSections] = useState([]);

  const [isDocumentModalOpen, setIsDocumentModalOpen] = useState(false);
  const [isSubsectionModalOpen, setIsSubsectionModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');
  const [newSectionName, setNewSectionName] = useState('');

  const handleAddDocument = (subsectionIndex) => {
    setIsDocumentModalOpen(true);
    setSectionToAdd(subsectionIndex);
  };

  const handleSaveDocument = () => {
    const docName = document.getElementById('docName').value;
    if (docName) {
      setSections(prevSections => {
        const updatedSections = [...prevSections];
        updatedSections[sectionToAdd].subsections[0].documents.push(docName);
        return updatedSections;
      });
    }
    setIsDocumentModalOpen(false);
  };

  const handleDeleteDocument = (subsectionIndex, docIndex) => {
    setSections(prevSections => {
      const updatedSections = [...prevSections];
      updatedSections[sectionToAdd].subsections[0].documents.splice(docIndex, 1);
      return updatedSections;
    });
  };

  const handleAddSubsection = (sectionIndex) => {
    setIsSubsectionModalOpen(true);
    setSectionToAdd(sectionIndex);
  };

  const handleSaveSubsection = () => {
    if (newSubsectionName) {
      setSections(prevSections => {
        const updatedSections = [...prevSections];
        updatedSections[sectionToAdd].subsections.push({ name: newSubsectionName, documents: [] });
        return updatedSections;
      });
    }
    setIsSubsectionModalOpen(false);
    setNewSubsectionName('');
  };

  const handleDeleteSubsection = (sectionIndex, subsectionIndex) => {
    setSections(prevSections => {
      const updatedSections = [...prevSections];
      updatedSections[sectionIndex].subsections.splice(subsectionIndex, 1);
      return updatedSections;
    });
  };

  const handleAddSection = () => {
    setNewSectionName('');
    setIsSubsectionModalOpen(true);
  };

  const handleSaveSection = () => {
    if (newSectionName) {
      setSections(prevSections => [...prevSections, { name: newSectionName, subsections: [{ name: 'Subsection 1', documents: [] }] }]);
    }
    setIsSubsectionModalOpen(false);
    setNewSectionName('');
  };

  const handleDeleteSection = (index) => {
    setSections(prevSections => prevSections.filter((_, i) => i !== index));
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      {sections.map((section, index) => (
        <div key={index} className="section">
          <h3>{section.name}</h3>
          {section.subsections.map((subsection, subIndex) => (
            <div key={subIndex} className="subsection">
              <h4>{subsection.name}</h4>
              <ul>
                {subsection.documents.map((doc, docIndex) => (
                  <li key={docIndex}>
                    {doc}
                    <button onClick={() => handleDeleteDocument(index, docIndex)}>
                      Delete
                    </button>
                  </li>
                ))}
              </ul>
              <button onClick={() => handleAddDocument(index)}>Add Document</button>
            </div>
          ))}
          <button onClick={() => handleAddSubsection(index)}>Add Subsection</button>
          <button onClick={() => handleDeleteSection(index)}>Delete Section</button>
        </div>
      ))}

      <button className="add-section-btn" onClick={handleAddSection}>
        Add Section
      </button>

      {isDocumentModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Document</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveDocument(); }}>
              <label>
                Document Name:
                <input
                  type="text"
                  id="docName"
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsDocumentModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}

      {isSubsectionModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Subsection</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveSubsection(); }}>
              <label>
                Subsection Name:
                <input
                  type="text"
                  value={newSubsectionName}
                  onChange={(e) => setNewSubsectionName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsSubsectionModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
