import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import fetchProjectName from '../utils/fetchProjectName';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();

  const [projectName, setProjectName] = useState('');
  const [sections, setSections] = useState([]);
  const [newSectionName, setNewSectionName] = useState('');
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [currentSectionId, setCurrentSectionId] = useState(null);
  const [currentSubsectionId, setCurrentSubsectionId] = useState(null);
  const [isSectionModalOpen, setIsSectionModalOpen] = useState(false);
  const [isSubsectionModalOpen, setIsSubsectionModalOpen] = useState(false);
  const [isDocumentModalOpen, setIsDocumentModalOpen] = useState(false);
  const [newDocumentName, setNewDocumentName] = useState('');
  const [filteredSections, setFilteredSections] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      if (projectId) {
        const success = await fetchProjectDetails();
        if (success) {
          console.log('Details fetched');
        } else {
          console.log('No details fetched');
        }
      }
    };
    fetchData();
  }, [projectId]);

  const capitalizeFirstLetter = (str) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  const fetchProjectDetails = async () => {
    try {
      const projectName = await fetchProjectName(projectId);
      setProjectName(capitalizeFirstLetter(projectName));
      await fetchSections(Number(projectId));
      return true;
    } catch (error) {
      console.error('Error fetching project details:', error);
      return false;
    }
  };

  const fetchSections = async (numericProjectId) => {
    try {
      const sectionsResponse = await axios.get(`http://localhost:5000/api/projects/${numericProjectId}/sections`);
      const sectionsWithSubsections = await Promise.all(
        sectionsResponse.data.map(async (section) => {
          const subsectionsResponse = await axios.get(
            `http://localhost:5000/api/sections/${section.section_id}/subsections`
          );
          const subsectionsWithDocuments = await Promise.all(
            subsectionsResponse.data.map(async (subsection) => {
              const documentsResponse = await axios.get(
                `http://localhost:5000/api/subsections/${subsection.subsection_id}/documents`
              );
              return { ...subsection, documents: documentsResponse.data };
            })
          );
          return { ...section, subsections: subsectionsWithDocuments };
        })
      );
      setSections(sectionsWithSubsections);
    } catch (error) {
      console.error('Error fetching sections:', error);
      throw error;
    }
  };

  const refetchProjectDetails = async () => {
    try {
      const projectName = await fetchProjectName(projectId);
      setProjectName(capitalizeFirstLetter(projectName));
      await fetchSections(Number(projectId));
    } catch (error) {
      console.error('Error refetching project details:', error);
    }
  };

  const handleAddSection = async () => {
    try {
      const numericProjectId = Number(projectId);
      await axios.post('http://localhost:5000/api/sections', {
        section_name: capitalizeFirstLetter(newSectionName),
        project_id: numericProjectId,
      });
      setNewSectionName('');
      setIsSectionModalOpen(false);
      refetchProjectDetails();
    } catch (error) {
      console.error('Error adding section:', error);
    }
  };

  const handleAddSubsection = async () => {
    try {
      await axios.post('http://localhost:5000/api/subsections', {
        subsection_name: capitalizeFirstLetter(newSubsectionName),
        section_id: currentSectionId,
        project_id: projectId,
      });
      setNewSubsectionName('');
      setIsSubsectionModalOpen(false);
      refetchProjectDetails();
    } catch (error) {
      console.error('Error adding subsection:', error);
    }
  };

  const handleSubmitDocument = async (e) => {
    e.preventDefault();
    try {
      const section = sections.find((section) => section.section_id === currentSectionId);
      const subsection = section.subsections.find(
        (subsection) => subsection.subsection_id === currentSubsectionId
      );

      await axios.post('http://localhost:5000/api/documents', {
        document_name: capitalizeFirstLetter(newDocumentName),
        subsection_id: currentSubsectionId,
        section_id: currentSectionId,
        project_id: projectId,
        project_name: projectName,
        section_name: section.section_name,
        subsection_name: subsection.subsection_name,
      });
      setNewDocumentName('');
      setIsDocumentModalOpen(false);
      refetchProjectDetails();
    } catch (error) {
      console.error('Error adding document:', error);
    }
  };

  const handleDeleteSection = async (sectionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/sections/${sectionId}`);
      refetchProjectDetails();
    } catch (error) {
      console.error('Error deleting section:', error);
    }
  };

  const handleDeleteSubsection = async (subsectionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/subsections/${subsectionId}`);
      refetchProjectDetails();
    } catch (error) {
      console.error('Error deleting subsection:', error);
    }
  };

  const handleDeleteDocument = async (documentId, subsectionId) => {
    try {
      console.log('Deleting document with ID:', documentId, 'in subsection ID:', subsectionId);
      await axios.delete(`http://localhost:5000/api/documents/${documentId}/${subsectionId}`, {
        data: { section_id: currentSectionId, project_id: projectId },
      });
      refetchProjectDetails();
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  };

  const handleBack = () => {
    navigate(-1);
  };

  const toggleSubsection = (subsectionId) => {
    setCurrentSubsectionId(currentSubsectionId === subsectionId ? null : subsectionId);
  };

  const toggleSection = (sectionId) => {
    setCurrentSectionId(currentSectionId === sectionId ? null : sectionId);
  };

  const handleDocumentClick = (documentId) => {
    navigate(`/document/${documentId}`);
  };

  return (
    <div className="project-detail-container">
      <h1 style={{ textAlign: 'center' }}>Project Details</h1>
      <h2 className="project-title">Project Title: {projectName}</h2>
      <button onClick={handleBack} className="back-button">
        Back
      </button>

      <div className="sections-container">
        <div className="sections-controls">
          <button onClick={() => setIsSectionModalOpen(true)} className="add-button">
            Add Section
          </button>
        </div>
        {sections.length > 0 ? (
          sections.map((section) => (
            <div key={section.section_id} className="section">
              <button className="section-name" onClick={() => toggleSection(section.section_id)}>
                {section.section_name}
                <button
                  className="delete-button"
                  onClick={() => handleDeleteSection(section.section_id)}
                >
                  Delete
                </button>
              </button>
              <button
                onClick={() => {
                  setCurrentSectionId(section.section_id);
                  setIsSubsectionModalOpen(true);
                }}
                className="add-button"
              >
                Add Subsection
              </button>

              <div
                className={
                  currentSectionId === section.section_id
                    ? 'subsection-container show'
                    : 'subsection-container hidden'
                }
              >
                {section.subsections.length > 0 ? (
                  section.subsections.map((subsection) => (
                    <div key={subsection.subsection_id} className="subsection">
                      <button
                        className="subsection-name"
                        onClick={() => toggleSubsection(subsection.subsection_id)}
                      >
                        {subsection.subsection_name}
                        <button
                          className="delete-button"
                          onClick={() => handleDeleteSubsection(subsection.subsection_id)}
                        >
                          Delete
                        </button>
                      </button>
                      <button
                        onClick={() => {
                          setCurrentSubsectionId(subsection.subsection_id);
                          setIsDocumentModalOpen(true);
                        }}
                        className="add-button"
                      >
                        Add Document
                      </button>

                      <div
                        className={
                          currentSubsectionId === subsection.subsection_id
                            ? 'document-container show'
                            : 'document-container hidden'
                        }
                      >
                        {subsection.documents.length > 0 ? (
                          subsection.documents.map((document) => (
                            <div key={document.document_id} className="document">
                              <ul>
                                <li
                                  onClick={() => handleDocumentClick(document.document_id)}
                                >
                                  {document.serial_number}-{document.document_name}
                                </li>
                              </ul>
                              <button
                                className="delete-button"
                                onClick={() =>
                                  handleDeleteDocument(document.document_id, subsection.subsection_id)
                                }
                              >
                                Delete
                              </button>
                            </div>
                          ))
                        ) : (
                          <p>No documents available.</p>
                        )}
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No subsections available.</p>
                )}
              </div>
            </div>
          ))
        ) : (
          <p>No sections available.</p>
        )}
      </div>

      {isSectionModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-content">
              <h2>Add Section</h2>
              <input
                type="text"
                value={newSectionName}
                onChange={(e) => setNewSectionName(e.target.value)}
                placeholder="Section Name"
                required
              />
              <button onClick={handleAddSection}>Add</button>
              <button onClick={() => setIsSectionModalOpen(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {isSubsectionModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-content">
              <h2>Add Subsection</h2>
              <input
                type="text"
                value={newSubsectionName}
                onChange={(e) => setNewSubsectionName(e.target.value)}
                placeholder="Subsection Name"
                required
              />
              <button onClick={handleAddSubsection}>Add</button>
              <button onClick={() => setIsSubsectionModalOpen(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {isDocumentModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-content">
              <h2>Add Document</h2>
              <form onSubmit={handleSubmitDocument}>
                <input
                  type="text"
                  value={newDocumentName}
                  onChange={(e) => setNewDocumentName(e.target.value)}
                  placeholder="Document Name"
                  required
                />
                <button type="submit">Add</button>
              </form>
              <button onClick={() => setIsDocumentModalOpen(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
