// src/components/ProjectDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [sections, setSections] = useState([]);
  const [subsections, setSubsections] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/projects/${projectName}/sections`);
      setSections(response.data);
      const subs = {};
      response.data.forEach(section => {
        subs[section.section_id] = [];
      });
      setSubsections(subs);
    } catch (error) {
      console.error('Error fetching sections:', error);
    }
  };

  const handleAddDocument = async (sectionId) => {
    const docName = prompt('Enter document name:');
    if (docName) {
      try {
        const response = await axios.post('http://localhost:5000/api/documents', { document_name: docName, subsection_id: sectionId });
        setSubsections(prev => ({
          ...prev,
          [sectionId]: [...prev[sectionId], response.data],
        }));
      } catch (error) {
        console.error('Error adding document:', error);
      }
    }
  };

  const handleDeleteDocument = async (sectionId, documentId) => {
    try {
      await axios.delete(`http://localhost:5000/api/documents/${documentId}`);
      setSubsections(prev => ({
        ...prev,
        [sectionId]: prev[sectionId].filter(doc => doc.document_id !== documentId),
      }));
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  };

  const handleAddSubsection = async (sectionName) => {
    setSectionToAdd(sectionName);
    setIsModalOpen(true);
  };

  const handleSaveSubsection = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/subsections', { subsection_name: newSubsectionName, section_id: sectionToAdd });
      setSubsections(prev => ({
        ...prev,
        [response.data.section_id]: [...prev[response.data.section_id], response.data],
      }));
    } catch (error) {
      console.error('Error adding subsection:', error);
    }
    setIsModalOpen(false);
  };

  const handleDeleteSubsection = async (sectionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/subsections/${sectionId}`);
      setSections(sections.filter(section => section.section_id !== sectionId));
      const updatedSubsections = { ...subsections };
      delete updatedSubsections[sectionId];
      setSubsections(updatedSubsections);
    } catch (error) {
      console.error('Error deleting subsection:', error);
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>Sections</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('New Section')}>
        Add Section
      </button>
      {sections.map(section => (
        <details key={section.section_id} className="section">
          <summary>
            {section.section_name}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection(section.section_id); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {subsections[section.section_id].map((doc, index) => (
                <li key={index}>
                  {doc.document_name}
                  <button onClick={() => handleDeleteDocument(section.section_id, doc.document_id)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument(section.section_id)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Section</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveSubsection(); }}>
              <label>
                Section Name:
                <input
                  type="text"
                  value={newSubsectionName}
                  onChange={(e) => setNewSubsectionName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
