import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [systemEngineering, setSystemEngineering] = useState([]);
  const [equipmentList, setEquipmentList] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/projects/${projectName}/sections`);
      setSystemEngineering(response.data.filter(section => section.type === 'System Engineering'));
      setEquipmentList(response.data.filter(section => section.type === 'Equipment List'));
    } catch (error) {
      console.error('Error fetching sections:', error);
    }
  };

  const handleAddDocument = async (sectionId, sectionType, subsectionName) => {
    const docName = prompt('Enter document name:');
    if (docName) {
      try {
        await axios.post(`http://localhost:3001/sections/${sectionId}/subsections`, { name: docName });
        fetchSections();
      } catch (error) {
        console.error('Error adding document:', error);
      }
    }
  };

  const handleDeleteDocument = async (sectionId, subsectionId) => {
    try {
      await axios.delete(`http://localhost:3001/subsections/${subsectionId}`);
      fetchSections();
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  };

  const handleAddSubsection = async (sectionId) => {
    const subsectionName = prompt('Enter subsection name:');
    if (subsectionName) {
      try {
        await axios.post(`http://localhost:3001/sections/${sectionId}/subsections`, { name: subsectionName });
        fetchSections();
      } catch (error) {
        console.error('Error adding subsection:', error);
      }
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>System Engineering</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('System Engineering')}>
        Add Section
      </button>
      {systemEngineering.map(section => (
        <details key={section.id} className="section">
          <summary>
            {section.name}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteDocument(section.id); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {section.subsections.map(subsection => (
                <li key={subsection.id}>
                  {subsection.name}
                  <button onClick={() => handleDeleteDocument(section.id, subsection.id)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument(section.id, 'System Engineering', section.name)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      <h3>Equipment List</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('Equipment List')}>
        Add Section
      </button>
      {equipmentList.map(section => (
        <details key={section.id} className="section">
          <summary>
            {section.name}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteDocument(section.id); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {section.subsections.map(subsection => (
                <li key={subsection.id}>
                  {subsection.name}
                  <button onClick={() => handleDeleteDocument(section.id, subsection.id)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument(section.id, 'Equipment List', section.name)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Subsection</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveSubsection(); }}>
              <label>
                Subsection Name:
                <input
                  type="text"
                  value={newSubsectionName}
                  onChange={(e) => setNewSubsectionName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
