import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ProjectTable.css';

const ProjectTable = ({ projects, deleteProject }) => {
  const navigate = useNavigate();

  const handleRowClick = (projectName) => {
    navigate(`/project/${projectName}`);
  };

  const deleteProjectFromBackend = async (projectId) => {
    try {
      const response = await fetch(`/api/projects/${projectId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error('Failed to delete project');
      }
      console.log('Project deleted successfully');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <table className="project-table">
      <thead>
        <tr>
          <th>Project Name</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {projects.map(project => (
          <tr key={project.id} onClick={() => handleRowClick(project.name)}>
            <td>{project.name}</td>
            <td>
              <button onClick={(e) => {
                e.stopPropagation();
                deleteProjectFromBackend(project.id);
                deleteProject(project.id);
              }}>
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ProjectTable;
