import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/ProjectTable.css';

const ProjectTable = ({ projects, fetchProjects }) => {
  const navigate = useNavigate();

  const handleRowClick = (projectName) => {
    navigate(`/project/${projectName}`);
  };

  const deleteProject = async (projectId) => {
    try {
      await axios.delete(`http://localhost:5000/projects/${projectId}`);
      fetchProjects(); // Refresh projects after deletion
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  return (
    <table className="project-table">
      <thead>
        <tr>
          <th>Project Name</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {projects.map(project => (
          <tr key={project.id} onClick={() => handleRowClick(project.name)}>
            <td>{project.name}</td>
            <td>
              <button onClick={(e) => { e.stopPropagation(); deleteProject(project.id); }}>Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ProjectTable;
