import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [systemEngineering, setSystemEngineering] = useState([]);
  const [equipmentList, setEquipmentList] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');

  useEffect(() => {
    fetchProjectDetails();
  }, []);

  const fetchProjectDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/projects/${projectName}`);
      setSystemEngineering(response.data.systemEngineering);
      setEquipmentList(response.data.equipmentList);
    } catch (error) {
      console.error('Error fetching project details:', error);
    }
  };

  const handleAddDocument = async (sectionId, subsectionId, docName) => {
    if (!docName) return;
    try {
      await axios.post(`http://localhost:3001/api/sections/${sectionId}/subsections/${subsectionId}/documents`, { name: docName });
      fetchProjectDetails(); // Refresh the data after adding
    } catch (error) {
      console.error('Error adding document:', error);
    }
  };

  const handleDeleteDocument = async (sectionId, subsectionId, docId) => {
    try {
      await axios.delete(`http://localhost:3001/api/sections/${sectionId}/subsections/${subsectionId}/documents/${docId}`);
      fetchProjectDetails(); // Refresh the data after deletion
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  };

  const handleAddSubsection = (section) => {
    setSectionToAdd(section);
    setIsModalOpen(true);
  };

  const handleSaveSubsection = async () => {
    if (!newSubsectionName) return;
    try {
      await axios.post(`http://localhost:3001/api/sections/${sectionToAdd}`, { name: newSubsectionName });
      setIsModalOpen(false);
      setNewSubsectionName('');
      setSectionToAdd('');
      fetchProjectDetails(); // Refresh the data after adding
    } catch (error) {
      console.error('Error adding subsection:', error);
    }
  };

  const handleDeleteSubsection = async (sectionId, subsectionId) => {
    try {
      await axios.delete(`http://localhost:3001/api/sections/${sectionId}/subsections/${subsectionId}`);
      fetchProjectDetails(); // Refresh the data after deletion
    } catch (error) {
      console.error('Error deleting subsection:', error);
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>System Engineering</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('System Engineering')}>
        Add Section
      </button>
      {systemEngineering.map(section => (
        <details key={section.id} className="section">
          <summary>
            {section.name}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('System Engineering', section.id); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            {section.subsections.map(subsection => (
              <div key={subsection.id}>
                <h4>{subsection.name}</h4>
                <ul>
                  {subsection.documents.map(doc => (
                    <li key={doc.id}>
                      {doc.name}
                      <button onClick={() => handleDeleteDocument(section.id, subsection.id, doc.id)}>Delete</button>
                    </li>
                  ))}
                </ul>
                <button className="add-document-btn" onClick={() => {
                  const docName = prompt('Enter document name:');
                  handleAddDocument(section.id, subsection.id, docName);
                }}>
                  Add Document
                </button>
              </div>
            ))}
          </div>
        </details>
      ))}

      <h3>Equipment List</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('Equipment List')}>
        Add Section
      </button>
      {equipmentList.map(section => (
        <details key={section.id} className="section">
          <summary>
            {section.name}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('Equipment List', section.id); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            {section.subsections.map(subsection => (
              <div key={subsection.id}>
                <h4>{subsection.name}</h4>
                <ul>
                  {subsection.documents.map(doc => (
                    <li key={doc.id}>
                      {doc.name}
                      <button onClick={() => handleDeleteDocument(section.id, subsection.id, doc.id)}>Delete</button>
                    </li>
                  ))}
                </ul>
                <button className="add-document-btn" onClick={() => {
                  const docName = prompt('Enter document name:');
                  handleAddDocument(section.id, subsection.id, docName);
                }}>
                  Add Document
                </button>
              </div>
            ))}
          </div>
        </details>
      ))}

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Subsection</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveSubsection(); }}>
              <label>
                Subsection Name:
                <input
                  type="text"
                  value={newSubsectionName}
                  onChange={(e) => setNewSubsectionName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
