import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();

  const [projectName, setProjectName] = useState('');
  const [sections, setSections] = useState([]);

  useEffect(() => {
    if (projectId) {
      fetchProjectDetails();
    }
  }, [projectId]);

  const fetchProjectDetails = async () => {
    try {
      // Fetch project details
      const projectResponse = await axios.get(`/api/projects/${projectId}`);
      setProjectName(projectResponse.data.project_name);

      // Fetch sections
      const sectionsResponse = await axios.get(`/api/projects/${projectId}/sections`);
      setSections(sectionsResponse.data);
    } catch (error) {
      console.error('Error fetching project details:', error);
    }
  };

  return (
    <div>
      <h1>Project Detail</h1>
      <p>Project Title: {projectName}</p>
      
      {/* Render sections and subsections */}
      <div>
        {sections.map(section => (
          <div key={section.section_id}>
            <h2>{section.section_name}</h2>
            <ul>
              {section.subsections.map(subsection => (
                <li key={subsection.subsection_id}>
                  {subsection.subsection_name}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectDetail;
