import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [systemEngineering, setSystemEngineering] = useState({
    Mech: [],
    Electrical: [],
    C_I: [],
  });

  const [equipmentList, setEquipmentList] = useState({
    'Equipment Subsection 1': [],
    'Equipment Subsection 2': [],
    'Equipment Subsection 3': [],
  });

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');

  const [isAddDocModalOpen, setIsAddDocModalOpen] = useState(false);
  const [newDocName, setNewDocName] = useState('');
  const [sectionForDoc, setSectionForDoc] = useState('');
  const [subsectionForDoc, setSubsectionForDoc] = useState('');

  useEffect(() => {
    fetch(`/api/projects/${projectName}/sections`)
      .then((res) => res.json())
      .then((data) => {
        const systemEngineeringSections = {};
        const equipmentListSections = {};
        data.forEach((section) => {
          if (section.name === 'System Engineering') {
            fetch(`/api/sections/${section.id}/subsections`)
              .then((res) => res.json())
              .then((subsections) => {
                subsections.forEach((subsection) => {
                  fetch(`/api/subsections/${subsection.id}/documents`)
                    .then((res) => res.json())
                    .then((docs) => {
                      systemEngineeringSections[subsection.name] = docs.map((doc) => doc.name);
                    });
                });
                setSystemEngineering(systemEngineeringSections);
              });
          } else if (section.name === 'Equipment List') {
            fetch(`/api/sections/${section.id}/subsections`)
              .then((res) => res.json())
              .then((subsections) => {
                subsections.forEach((subsection) => {
                  fetch(`/api/subsections/${subsection.id}/documents`)
                    .then((res) => res.json())
                    .then((docs) => {
                      equipmentListSections[subsection.name] = docs.map((doc) => doc.name);
                    });
                });
                setEquipmentList(equipmentListSections);
              });
          }
        });
      });
  }, [projectName]);

  const handleAddDocument = (section, subsection) => {
    setSectionForDoc(section);
    setSubsectionForDoc(subsection);
    setIsAddDocModalOpen(true);
  };

  const handleSaveDocument = () => {
    if (newDocName) {
      if (sectionForDoc === 'System Engineering') {
        const sectionId = Object.keys(systemEngineering).find((key) => key === subsectionForDoc);
        fetch('/api/documents', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ subsectionId: sectionId, name: newDocName }),
        }).then(() => {
          setSystemEngineering((prev) => ({
            ...prev,
            [subsectionForDoc]: [...prev[subsectionForDoc], newDocName],
          }));
          setIsAddDocModalOpen(false);
          setNewDocName('');
        });
      } else if (sectionForDoc === 'Equipment List') {
        const sectionId = Object.keys(equipmentList).find((key) => key === subsectionForDoc);
        fetch('/api/documents', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ subsectionId: sectionId, name: newDocName }),
        }).then(() => {
          setEquipmentList((prev) => ({
            ...prev,
            [subsectionForDoc]: [...prev[subsectionForDoc], newDocName],
          }));
          setIsAddDocModalOpen(false);
          setNewDocName('');
        });
      }
    }
  };

  const handleDeleteDocument = (section, subsection, index) => {
    if (section === 'System Engineering') {
      const docId = systemEngineering[subsection][index];
      fetch(`/api/documents/${docId}`, {
        method: 'DELETE',
      }).then(() => {
        setSystemEngineering((prev) => ({
          ...prev,
          [subsection]: prev[subsection].filter((_, i) => i !== index),
        }));
      });
    } else if (section === 'Equipment List') {
      const docId = equipmentList[subsection][index];
      fetch(`/api/documents/${docId}`, {
        method: 'DELETE',
      }).then(() => {
        setEquipmentList((prev) => ({
          ...prev,
          [subsection]: prev[subsection].filter((_, i) => i !== index),
        }));
      });
    }
  };

  const handleAddSubsection = (section) => {
    setSectionToAdd(section);
    setIsModalOpen(true);
  };

  const handleSaveSubsection = () => {
    const sectionId = sectionToAdd === 'System Engineering' ? systemEngineering : equipmentList;
    fetch('/api/subsections', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ sectionId: sectionToAdd, name: newSubsectionName }),
    }).then(() => {
      if (sectionToAdd === 'System Engineering') {
        setSystemEngineering((prev) => ({
          ...prev,
          [newSubsectionName]: [],
        }));
      } else if (sectionToAdd === 'Equipment List') {
        setEquipmentList((prev) => ({
          ...prev,
          [newSubsectionName]: [],
        }));
      }
      setIsModalOpen(false);
      setNewSubsectionName('');
      setSectionToAdd('');
    });
  };

  const handleDeleteSubsection = (section, subsection) => {
    const sectionId = section === 'System Engineering' ? systemEngineering : equipmentList;
    const subsectionId = sectionId[subsection];
    fetch(`/api/subsections/${subsectionId}`, {
      method: 'DELETE',
    }).then(() => {
      if (section === 'System Engineering') {
        setSystemEngineering((prev) => {
          const updated = { ...prev };
          delete updated[subsection];
          return updated;
        });
      } else if (section === 'Equipment List') {
        setEquipmentList((prev) => {
          const updated = { ...prev };
          delete updated[subsection];
          return updated;
        });
      }
    });
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>System Engineering</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('System Engineering')}>
        Add Section
      </button>
      {Object.keys(systemEngineering).map((subsection) => (
        <details key={subsection} className="section">
          <summary>
            {subsection}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('System Engineering', subsection); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {systemEngineering[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('System Engineering', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument('System Engineering', subsection)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      <h3>Equipment List</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('Equipment List')}>
        Add Section
      </button>
      {Object.keys(equipmentList).map((subsection) => (
        <details key={subsection} className="section">
          <summary>
            {subsection}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('Equipment List', subsection); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {equipmentList[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('Equipment List', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument('Equipment List', subsection)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Subsection</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveSubsection(); }}>
              <label>
                Subsection Name:
                <input
                  type="text"
                  value={newSubsectionName}
                  onChange={(e) => setNewSubsectionName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}

      {isAddDocModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Document</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveDocument(); }}>
              <label>
                Document Name:
                <input
                  type="text"
                  value={newDocName}
                  onChange={(e) => setNewDocName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsAddDocModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
