import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [systemEngineering, setSystemEngineering] = useState({
    Mech: [],
    Electrical: [],
    "C&I": [], 
  });

  const [equipmentList, setEquipmentList] = useState({
    Equipment1: [],
    Equipment2: [],
    Equipment3: [],
  });

  const [isDocumentModalOpen, setIsDocumentModalOpen] = useState(false);
  const [isSectionModalOpen, setIsSectionModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');
  const [newSectionName, setNewSectionName] = useState('');

  const handleAddDocument = (section, subsection) => {
    setIsDocumentModalOpen(true);
    setSectionToAdd(section);
    setNewSubsectionName(subsection);
  };

  const handleSaveDocument = () => {
    const docName = document.getElementById('docName').value;
    if (docName) {
      if (sectionToAdd === 'System Engineering') {
        setSystemEngineering(prev => ({
          ...prev,
          [newSubsectionName]: [...prev[newSubsectionName], docName],
        }));
      } else if (sectionToAdd === 'Equipment List') {
        setEquipmentList(prev => ({
          ...prev,
          [newSubsectionName]: [...prev[newSubsectionName], docName],
        }));
      }
    }
    setIsDocumentModalOpen(false);
  };

  const handleDeleteDocument = (section, subsection, index) => {
    if (section === 'System Engineering') {
      setSystemEngineering(prev => ({
        ...prev,
        [subsection]: prev[subsection].filter((_, i) => i !== index),
      }));
    } else if (section === 'Equipment List') {
      setEquipmentList(prev => ({
        ...prev,
        [subsection]: prev[subsection].filter((_, i) => i !== index),
      }));
    }
  };

  const handleAddSubsection = (section) => {
    setSectionToAdd(section);
    setIsSectionModalOpen(true);
  };

  const handleSaveSubsection = () => {
    if (newSectionName) {
      if (sectionToAdd === 'System Engineering') {
        setSystemEngineering(prev => ({
          ...prev,
          [newSectionName]: [],
        }));
      } else if (sectionToAdd === 'Equipment List') {
        setEquipmentList(prev => ({
          ...prev,
          [newSectionName]: [],
        }));
      }
    }
    setIsSectionModalOpen(false);
    setNewSectionName('');
  };

  const handleDeleteSubsection = (section, subsection) => {
    if (section === 'System Engineering') {
      setSystemEngineering(prev => {
        const updated = { ...prev };
        delete updated[subsection];
        return updated;
      });
    } else if (section === 'Equipment List') {
      setEquipmentList(prev => {
        const updated = { ...prev };
        delete updated[subsection];
        return updated;
      });
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>System Engineering</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('System Engineering')}>
        Add Section
      </button>
      {Object.keys(systemEngineering).map(subsection => (
        <details key={subsection} className="section">
          <summary>
            {subsection}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('System Engineering', subsection); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {systemEngineering[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('System Engineering', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument('System Engineering', subsection)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      <h3>Equipment List</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('Equipment List')}>
        Add Section
      </button>
      {Object.keys(equipmentList).map(subsection => (
        <details key={subsection} className="section">
          <summary>
            {subsection}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('Equipment List', subsection); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {equipmentList[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('Equipment List', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument('Equipment List', subsection)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      {isDocumentModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Document</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveDocument(); }}>
              <label>
                Document Name:
                <input
                  type="text"
                  id="docName"
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsDocumentModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}

      {isSectionModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Section</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveSubsection(); }}>
              <label>
                Section Name:
                <input
                  type="text"
                  value={newSectionName}
                  onChange={(e) => setNewSectionName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsSectionModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
