import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [sections, setSections] = useState([]);

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/projects/${projectName}/sections`);
      setSections(response.data);
    } catch (error) {
      console.error('Error fetching sections:', error);
    }
  };

  const handleAddSection = async () => {
    try {
      await axios.post('http://localhost:5000/api/sections', { section_name: 'New Section', project_name: projectName });
      fetchSections(); // Refresh sections after adding a new one
    } catch (error) {
      console.error('Error adding section:', error);
    }
  };

  const handleAddSubsection = async (sectionId) => {
    try {
      await axios.post('http://localhost:5000/api/subsections', { subsection_name: 'New Subsection', section_id: sectionId });
      fetchSections(); // Refresh sections after adding a new one
    } catch (error) {
      console.error('Error adding subsection:', error);
    }
  };

  const handleAddDocument = async (subsectionId) => {
    const docName = prompt('Enter document name:');
    if (docName) {
      try {
        await axios.post('http://localhost:5000/api/documents', { document_name: docName, subsection_id: subsectionId });
        fetchSections(); // Refresh sections after adding a new one
      } catch (error) {
        console.error('Error adding document:', error);
      }
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>Sections</h3>
      <button className="add-section-btn" onClick={handleAddSection}>
        Add Section
      </button>
      {sections.map(section => (
        <div key={section.section_id} className="section">
          <h4>{section.section_name}</h4>
          <button className="add-subsection-btn" onClick={() => handleAddSubsection(section.section_id)}>
            Add Subsection
          </button>
          {section.subsections.map(subsection => (
            <div key={subsection.subsection_id} className="subsection">
              <h5>{subsection.subsection_name}</h5>
              <button className="add-document-btn" onClick={() => handleAddDocument(subsection.subsection_id)}>
                Add Document
              </button>
              {/* List documents here if needed */}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default ProjectDetail;
