import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [sections, setSections] = useState([]);

  const [isDocumentModalOpen, setIsDocumentModalOpen] = useState(false);
  const [isSectionModalOpen, setIsSectionModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');
  const [newSectionName, setNewSectionName] = useState('');

  const handleAddDocument = (section) => {
    setIsDocumentModalOpen(true);
    setSectionToAdd(section);
  };

  const handleSaveDocument = () => {
    const docName = document.getElementById('docName').value;
    if (docName) {
      setSections(prevSections => {
        const updatedSections = [...prevSections];
        const index = updatedSections.findIndex(sec => sec.name === sectionToAdd);
        if (index !== -1) {
          updatedSections[index].documents.push(docName);
        }
        return updatedSections;
      });
    }
    setIsDocumentModalOpen(false);
  };

  const handleDeleteDocument = (sectionIndex, docIndex) => {
    setSections(prevSections => {
      const updatedSections = [...prevSections];
      updatedSections[sectionIndex].documents.splice(docIndex, 1);
      return updatedSections;
    });
  };

  const handleAddSection = () => {
    setIsSectionModalOpen(true);
  };

  const handleSaveSection = () => {
    if (newSectionName) {
      setSections(prevSections => [...prevSections, { name: newSectionName, documents: [] }]);
    }
    setIsSectionModalOpen(false);
    setNewSectionName('');
  };

  const handleDeleteSection = (index) => {
    setSections(prevSections => prevSections.filter((_, i) => i !== index));
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      {sections.map((section, index) => (
        <details key={index} className="section">
          <summary>
            {section.name}
            <button className="delete-section-btn" onClick={() => handleDeleteSection(index)}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {section.documents.map((doc, docIndex) => (
                <li key={docIndex}>
                  {doc}
                  <button onClick={() => handleDeleteDocument(index, docIndex)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument(section.name)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      <button className="add-section-btn" onClick={handleAddSection}>
        Add Section
      </button>

      {isDocumentModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Document</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveDocument(); }}>
              <label>
                Document Name:
                <input
                  type="text"
                  id="docName"
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsDocumentModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}

      {isSectionModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Section</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveSection(); }}>
              <label>
                Section Name:
                <input
                  type="text"
                  value={newSectionName}
                  onChange={(e) => setNewSectionName(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsSectionModalOpen(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
