import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectTable from './ProjectTable';
import ProjectFormModal from './ProjectFormModal';
import '../styles/Home.css'; // Ensure this file exists

const Home = () => {
  const [projects, setProjects] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:5000/projects');
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const addProject = async (projectName) => {
    try {
      const response = await axios.post('http://localhost:5000/projects', {
        project_name: projectName
      });
      const newProject = { id: response.data.id, name: projectName };
      setProjects([...projects, newProject]);
    } catch (error) {
      console.error('Error adding project:', error);
    }
  };

  const deleteProject = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/projects/${id}`);
      setProjects(projects.filter(project => project.id !== id));
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  return (
    <div className="home">
      <h1>Project Master List</h1>
      <button onClick={() => setIsModalOpen(true)}>Add New Project</button>
      <ProjectTable projects={projects} deleteProject={deleteProject} />
      {isModalOpen && (
        <ProjectFormModal
          onClose={() => setIsModalOpen(false)}
          onSave={addProject}
        />
      )}
    </div>
  );
};

export default Home;
