// src/components/ProjectDetail.js
import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [systemEngineering, setSystemEngineering] = useState({
    Mech: [],
    Electrical: [],
    C_I: [],
  });

  const [equipmentList, setEquipmentList] = useState({
    Equipment1: [],
    Equipment2: [],
    Equipment3: [],
  });

  const handleAddDocument = (section, subsection) => {
    const docName = prompt('Enter document name:');
    if (docName) {
      if (section === 'System Engineering') {
        setSystemEngineering(prev => ({
          ...prev,
          [subsection]: [...prev[subsection], docName],
        }));
      } else if (section === 'Equipment List') {
        setEquipmentList(prev => ({
          ...prev,
          [subsection]: [...prev[subsection], docName],
        }));
      }
    }
  };

  const handleDeleteDocument = (section, subsection, index) => {
    if (section === 'System Engineering') {
      setSystemEngineering(prev => ({
        ...prev,
        [subsection]: prev[subsection].filter((_, i) => i !== index),
      }));
    } else if (section === 'Equipment List') {
      setEquipmentList(prev => ({
        ...prev,
        [subsection]: prev[subsection].filter((_, i) => i !== index),
      }));
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>System Engineering</h3>
      {['Mech', 'Electrical', 'C_I'].map(subsection => (
        <details key={subsection} className="section">
          <summary>{subsection}</summary>
          <div className="subsection-content">
            <button onClick={() => handleAddDocument('System Engineering', subsection)}>
              Add Document
            </button>
            <ul>
              {systemEngineering[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('System Engineering', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </details>
      ))}

      <h3>Equipment List</h3>
      {['Equipment1', 'Equipment2', 'Equipment3'].map(subsection => (
        <details key={subsection} className="section">
          <summary>{subsection}</summary>
          <div className="subsection-content">
            <button onClick={() => handleAddDocument('Equipment List', subsection)}>
              Add Document
            </button>
            <ul>
              {equipmentList[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('Equipment List', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </details>
      ))}
    </div>
  );
};

export default ProjectDetail;
