import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/DocumentDetail.css';

const DocumentDetail = () => {
  const { documentId } = useParams();
  const [document, setDocument] = useState(null);

  useEffect(() => {
    const fetchDocumentDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/documents/${documentId}`);
        setDocument(response.data);
      } catch (error) {
        console.error('Error fetching document details:', error);
      }
    };

    fetchDocumentDetails();
  }, [documentId]);

  const handleEdit = async () => {
    // Implement edit functionality here
  };

  if (!document) {
    return <div>Loading...</div>;
  }

  return (
    <div className="document-detail-container">
      <h1>{document.document_name}</h1>
      <table>
        <thead>
          <tr>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Status</th>
            <th>Priority</th>
            <th>Assigned To</th>
            <th>Progress</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{document.start_date}</td>
            <td>{document.end_date}</td>
            <td>{document.status}</td>
            <td>{document.priority}</td>
            <td>{document.assigned_to}</td>
            <td>
              <progress value={document.progress} max="100"></progress>
            </td>
            <td>
              <button onClick={handleEdit}>Edit</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default DocumentDetail;
