import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [systemEngineering, setSystemEngineering] = useState({});
  const [equipmentList, setEquipmentList] = useState({});
  const [isDocumentModalOpen, setIsDocumentModalOpen] = useState(false);
  const [isSectionModalOpen, setIsSectionModalOpen] = useState(false);
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [sectionToAdd, setSectionToAdd] = useState('');
  const [newSectionName, setNewSectionName] = useState('');

  useEffect(() => {
    fetchDetails();
  }, []);

  const fetchDetails = async () => {
    try {
      const projectIdResponse = await axios.get(`http://localhost:5000/api/projects?name=${projectName}`);
      const projectId = projectIdResponse.data[0].project_id;

      const seResponse = await axios.get(`http://localhost:5000/api/system-engineering/${projectId}`);
      const eqResponse = await axios.get(`http://localhost:5000/api/equipment/${projectId}`);

      setSystemEngineering(groupByType(seResponse.data));
      setEquipmentList(groupBySubsection(eqResponse.data));
    } catch (error) {
      console.error('Error fetching details:', error);
    }
  };

  const groupByType = (data) => {
    return data.reduce((acc, item) => {
      acc[item.se_type] = acc[item.se_type] || [];
      acc[item.se_type].push(item.se_details);
      return acc;
    }, {});
  };

  const groupBySubsection = (data) => {
    return data.reduce((acc, item) => {
      acc[item.eq_subsection] = acc[item.eq_subsection] || [];
      acc[item.eq_subsection].push(item.equipment_name);
      return acc;
    }, {});
  };

  const handleAddDocument = (section, subsection) => {
    setIsDocumentModalOpen(true);
    setSectionToAdd(section);
    setNewSubsectionName(subsection);
  };

  const handleSaveDocument = async () => {
    const docName = document.getElementById('docName').value;
    if (docName) {
      const projectIdResponse = await axios.get(`http://localhost:5000/api/projects?name=${projectName}`);
      const projectId = projectIdResponse.data[0].project_id;

      if (sectionToAdd === 'System Engineering') {
        await axios.post('http://localhost:5000/api/system-engineering', {
          project_id: projectId,
          se_type: newSubsectionName,
          se_details: docName,
        });
      } else if (sectionToAdd === 'Equipment List') {
        await axios.post('http://localhost:5000/api/equipment', {
          project_id: projectId,
          equipment_name: docName,
          eq_subsection: newSubsectionName,
        });
      }
      fetchDetails();
    }
    setIsDocumentModalOpen(false);
  };

  const handleDeleteDocument = async (section, subsection, index) => {
    const projectIdResponse = await axios.get(`http://localhost:5000/api/projects?name=${projectName}`);
    const projectId = projectIdResponse.data[0].project_id;

    if (section === 'System Engineering') {
      const seType = subsection;
      const seDetails = systemEngineering[subsection][index];
      const seResponse = await axios.get(`http://localhost:5000/api/system-engineering/${projectId}`);
      const seId = seResponse.data.find(se => se.se_details === seDetails && se.se_type === seType).se_id;
      await axios.delete(`http://localhost:5000/api/system-engineering/${seId}`);
    } else if (section === 'Equipment List') {
      const eqSubsection = subsection;
      const eqName = equipmentList[subsection][index];
      const eqResponse = await axios.get(`http://localhost:5000/api/equipment/${projectId}`);
      const eqId = eqResponse.data.find(eq => eq.equipment_name === eqName && eq.eq_subsection === eqSubsection).equipment_id;
      await axios.delete(`http://localhost:5000/api/equipment/${eqId}`);
    }
    fetchDetails();
  };

  const handleAddSubsection = (section) => {
    setSectionToAdd(section);
    setIsSectionModalOpen(true);
  };

  const handleSaveSubsection = () => {
    if (newSectionName) {
      if (sectionToAdd === 'System Engineering') {
        setSystemEngineering(prev => ({
          ...prev,
          [newSectionName]: [],
        }));
      } else if (sectionToAdd === 'Equipment List') {
        setEquipmentList(prev => ({
          ...prev,
          [newSectionName]: [],
        }));
      }
    }
    setIsSectionModalOpen(false);
    setNewSectionName('');
  };

  const handleDeleteSubsection = (section, subsection) => {
    if (section === 'System Engineering') {
      setSystemEngineering(prev => {
        const updated = { ...prev };
        delete updated[subsection];
        return updated;
      });
    } else if (section === 'Equipment List') {
      setEquipmentList(prev => {
        const updated = { ...prev };
        delete updated[subsection];
        return updated;
      });
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>System Engineering</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('System Engineering')}>
        Add Section
      </button>
      {Object.keys(systemEngineering).map(subsection => (
        <details key={subsection} className="section">
          <summary>
            {subsection}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('System Engineering', subsection); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {systemEngineering[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('System Engineering', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument('System Engineering', subsection)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      <h3>Equipment List</h3>
      <button className="add-section-btn" onClick={() => handleAddSubsection('Equipment List')}>
        Add Section
      </button>
      {Object.keys(equipmentList).map(subsection => (
        <details key={subsection} className="section">
          <summary>
            {subsection}
            <button className="delete-section-btn" onClick={(e) => { e.stopPropagation(); handleDeleteSubsection('Equipment List', subsection); }}>
              Delete Section
            </button>
          </summary>
          <div className="subsection-content">
            <ul>
              {equipmentList[subsection].map((doc, index) => (
                <li key={index}>
                  {doc}
                  <button onClick={() => handleDeleteDocument('Equipment List', subsection, index)}>
                    Delete
                  </button>
                </li>
              ))}
            </ul>
            <button className="add-document-btn" onClick={() => handleAddDocument('Equipment List', subsection)}>
              Add Document
            </button>
          </div>
        </details>
      ))}

      {isDocumentModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Add New Document</h2>
            <label>
              Document Name:
              <input type="text" id="docName" />
            </label>
            <button onClick={handleSaveDocument}>Save</button>
            <button onClick={() => setIsDocumentModalOpen(false)}>Cancel</button>
          </div>
        </div>
      )}

      {isSectionModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Add New Section</h2>
            <label>
              Section Name:
              <input type="text" value={newSectionName} onChange={(e) => setNewSectionName(e.target.value)} />
            </label>
            <button onClick={handleSaveSubsection}>Save</button>
            <button onClick={() => setIsSectionModalOpen(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
