import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/ProjectDetail.css';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [sections, setSections] = useState([]);
  const [newSectionName, setNewSectionName] = useState('');
  const [newSubsectionName, setNewSubsectionName] = useState('');
  const [currentSectionId, setCurrentSectionId] = useState(null);
  const [isSectionModalOpen, setIsSectionModalOpen] = useState(false);
  const [isSubsectionModalOpen, setIsSubsectionModalOpen] = useState(false);

  useEffect(() => {
    fetchSections();
  }, []);

  const fetchSections = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/projects/${projectName}/sections`);
      setSections(response.data);
    } catch (error) {
      console.error('Error fetching sections:', error);
    }
  };

  const handleAddSection = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/sections', { section_name: newSectionName, project_name: projectName });
      const newSection = response.data;
      setSections(prevSections => [...prevSections, newSection]);
      setIsSectionModalOpen(false);
      setNewSectionName('');
    } catch (error) {
      console.error('Error adding section:', error);
    }
  };

  const handleAddSubsection = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/subsections', { subsection_name: newSubsectionName, section_id: currentSectionId });
      const newSubsection = response.data;
      setSections(prevSections => {
        return prevSections.map(section => {
          if (section.section_id === currentSectionId) {
            return { ...section, subsections: [...section.subsections, newSubsection] };
          }
          return section;
        });
      });
      setIsSubsectionModalOpen(false);
      setNewSubsectionName('');
    } catch (error) {
      console.error('Error adding subsection:', error);
    }
  };

  const handleAddDocument = async (subsectionId) => {
    const docName = prompt('Enter document name:');
    if (docName) {
      try {
        const response = await axios.post('http://localhost:5000/api/documents', { document_name: docName, subsection_id: subsectionId });
        const newDocument = response.data;
        setSections(prevSections => {
          return prevSections.map(section => {
            const updatedSubsections = section.subsections.map(subsection => {
              if (subsection.subsection_id === subsectionId) {
                return { ...subsection, documents: [...subsection.documents, newDocument] };
              }
              return subsection;
            });
            return { ...section, subsections: updatedSubsections };
          });
        });
      } catch (error) {
        console.error('Error adding document:', error);
      }
    }
  };

  return (
    <div className="project-detail">
      <h2>Project Detail</h2>
      <p>Project Title: {projectName}</p>
      <button onClick={() => navigate(-1)}>Back</button>

      <h3>Sections</h3>
      <button onClick={() => setIsSectionModalOpen(true)}>Add Section</button>

      {sections.map(section => (
        <div key={section.section_id} className="section">
          <h4>{section.section_name}</h4>
          <button onClick={() => { setCurrentSectionId(section.section_id); setIsSubsectionModalOpen(true); }}>Add Subsection</button>
          <ul>
            {section.subsections.map(subsection => (
              <li key={subsection.subsection_id}>
                <h5>{subsection.subsection_name}</h5>
                <button onClick={() => handleAddDocument(subsection.subsection_id)}>Add Document</button>
                <ul>
                  {subsection.documents.map(document => (
                    <li key={document.document_id}>{document.document_name}</li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      ))}

      {isSectionModalOpen && (
        <div className="modal">
          <h3>Add Section</h3>
          <input type="text" value={newSectionName} onChange={(e) => setNewSectionName(e.target.value)} />
          <button onClick={handleAddSection}>Save</button>
          <button onClick={() => setIsSectionModalOpen(false)}>Cancel</button>
        </div>
      )}

      {isSubsectionModalOpen && (
        <div className="modal">
          <h3>Add Subsection</h3>
          <input type="text" value={newSubsectionName} onChange={(e) => setNewSubsectionName(e.target.value)} />
          <button onClick={handleAddSubsection}>Save</button>
          <button onClick={() => setIsSubsectionModalOpen(false)}>Cancel</button>
          </div>
        )}
      </div>
    );
  };
  
  export default ProjectDetail;
